soup = BeautifulSoup(doc, 'html.parser')

top_categories = {}

level_1 = [['Уровень 1', 'Ссылка']]
level_2 = [['Уровень 1', 'Уровень 2', 'Ссылка']]
level_3 = [['Уровень 1', 'Уровень 2', 'Уровень 3', 'Ссылка']]

for top_category in soup.select('#top-menu > ul > li'):
    top_categories[top_category['data-menu-id']] = {'name': top_category.a.contents[0], 'link': top_category.a['href']}

for level_1_item in soup.select('#submenu > ul'):
    menu_id = level_1_item['data-menu-id']

    #  Дропаем баннеры
    if 'i-topmenus-banners' in level_1_item['class']:
        continue

    for level_2_item in level_1_item.children:
        #  Дропаем баннеры
        if 'menu-banner' in level_2_item['class']:
            level_2_name = ''  # Нужно обнулить

            continue

        #  Дропаем перечисления брендов
        if 'list-letters' in level_2_item['class']:
            level_2_name = ''  # Нужно обнулить

            continue

        #  Дропаем страницы брендов
        if 'specbrands' in level_2_item['class']:
            level_2_name = ''  # Нужно обнулить

            continue

        if 'categories' in level_2_item['class']:
            level_2_name = ''  # Нужно обнулить

            # Такой случай нужно обрабатывать по-особому, там нет вложенного меню
            for level_3_item in level_2_item.select('li a'):
                # Записываем в табличку уровня 3
                level_3.append([top_categories[menu_id]['name'], level_2_name, level_3_item.contents[0], level_3_item['href']])

            continue

        if 'category' in level_2_item['class']:
            # Некоторые категории – это продолжение (вторая колонка) категорий, объявленных в предыдущей колонке
            level_2_name = level_2_item.a.contents[0] if level_2_item.a else ''

        for level_3_item in level_2_item.select('ul li a'):
            # Записываем в табличку уровня 3
            level_3.append([top_categories[menu_id]['name'], level_2_name, level_3_item.contents[0], level_3_item['href']])

        #  Записываем в табличку уровня 2
        if level_2_item.a:
            level_2.append([top_categories[menu_id]['name'], level_2_item.a.contents[0], level_2_item.a['href']])

    # Записываем в табличку уровня 1
    level_1.append([top_categories[menu_id]['name'], top_categories[menu_id]['link']])

save_csv('artifacts/level_1.csv', level_1)
save_csv('artifacts/level_2.csv', level_2)
save_csv('artifacts/level_3.csv', level_3)